mapper.update:
update t_cart
set buy_yn = 1
where cart_id = #{cartId}

update t_order
set order_status = #{orderStatus}
where payment = 1

mapper.select:
SELECT sum(A.quantity * B.price) sumPrice
from t_cart A
inner JOIN t_product B
ON A.product_id = B.product_id
WHERE user_id = #{userId}
order by userId;
